CREATE TABLE Foo (
  FooID STRING(36) NOT NULL,
  Name STRING(MAX),
) PRIMARY KEY(FooID);

CREATE TABLE Bar (
  FooID STRING(36) NOT NULL,
  BarID STRING(36) NOT NULL,
  Name STRING(MAX),
) PRIMARY KEY(FooID, BarID),
  INTERLEAVE IN PARENT Foo ON DELETE CASCADE;

CREATE TABLE Baz (
  FooID STRING(36) NOT NULL,
  BarID STRING(36) NOT NULL,
  BazID STRING(36) NOT NULL,
  Name STRING(MAX),
) PRIMARY KEY(FooID, BarID, BazID),
  INTERLEAVE IN PARENT Bar ON DELETE CASCADE;

CREATE TABLE Boo (
  BooID STRING(36) NOT NULL,
  BazID STRING(36) NOT NULL,
  Name STRING(MAX),
  CONSTRAINT FK_BooBaz FOREIGN KEY (BazID) REFERENCES Baz (BazID)
) PRIMARY KEY(BooID);

CREATE TABLE AllTypes (
  ID STRING(MAX) NOT NULL,
  BoolValue BOOL,
  Int64Value INT64,
  Float64Value FLOAT64,
  TimestampValue TIMESTAMP,
  DateValue DATE,
  StringValue STRING(MAX),
  BytesValue BYTES(MAX),
  NumericValue NUMERIC,
  JSONValue JSON,
  StringArray ARRAY<STRING(MAX)>,
  BoolArray ARRAY<Bool>,
  Int64Array ARRAY<INT64>,
  Float64Array ARRAY<FLOAT64>,
  TimestampArray ARRAY<TIMESTAMP>,
  DateArray ARRAY<DATE>,
  BytesArray ARRAY<BYTES(MAX)>,
  NumericArray ARRAY<NUMERIC>,
  JSONArray ARRAY<JSON>,
) PRIMARY KEY(ID);
